      out.write("\t\t$slides.css('height',height);\r\n");
      out.write("\t}\r\n");
      out.write("\r\n");
      out.write("\tfunction startSlider(action, dotNum) {\r\n");
      out.write("\t\tif(action == 'prv'){\r\n");
      out.write("\t\t\t$slideUl.animate({'margin-left': '+='+width}, animationSpeed, function() {\r\n");
